let form=document.getElementById("form");form.addEventListener("submit",e=>{e.preventDefault(),validateFunction();const emailValue=document.querySelector('[name = "E-mail"]').value,passwordValue=document.querySelector('[name = "Password"]').value;localStorage.setItem("email",emailValue),localStorage.setItem("password",passwordValue);const storedEmail=localStorage.getItem("email"),storedPassword=localStorage.getItem("password");alert(`Email: ${storedEmail} \nPassword: ${storedPassword}`)});var inputs=document.querySelectorAll(".inputField"),correctPassword;function validateFunction(){inputs.forEach(input=>{const inputValue=input.value;console.log(inputValue,"value1");const inputName=input.getAttribute("name"),smallElement=input.parentElement.querySelector("small"),showError=errorMessage=>{isInValid(input),smallElement.innerText=errorMessage},showVaild=()=>{isValid(input),smallElement.innerText=""};if(""===inputValue)showError(`${inputName} cannot blank`);else switch(inputName){case"E-mail":const emailRegx=/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;emailRegx.test(inputValue)?showVaild():showError("you have invaild email address");break;case"Password":var regex;new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*_])(?=.{6,12}$)").test(inputValue)?(showVaild(),correctPassword=inputValue):showError(`${inputName}must be 1 uppercase,1 lowecase, 1number and special characters and limit 6 to 8 letter`);break;case"confirm Password ":correctPassword!==inputValue?showError(`${inputName} not equal to password`):showVaild();break;default:showVaild()}})}function isInValid(input){input.classList.add("is-invalid")}function isValid(input){input.classList.add("is-valid"),input.classList.remove("is-invalid")}document.getElementById("reset").addEventListener("click",e=>{var smallTags;document.querySelectorAll(".small").forEach(smallTag=>{smallTag.classList.remove("is-invalid"),smallTag.classList.remove("is-valid"),smallTag.innerText="",localStorage.clear()})});